 class Program
 {
 			static void Main()
 			{
 						IObservable subject = new ConcreteSubject();

 						subject.RegisterObserver( new ConcreteObserver( subject ) );

 						subject.RegisterObserver( new ConcreteObserver( subject ) );

 						subject.State = "nеw state";

 						subject.NotifyObserver();
 			}
 }

 interface IObservable
 {
 			void RegisterObserver();
 			void RemoveObserver();
 			void NotifyObserver();
 			string State {get; set;}
 }

 interface IObserver
 {
 			void Update();
 }

 ConcreteObservable : IObservable
 {
 			ArrayList observers = new ArrayList();
 			public void RegisterObserver( IObserver observer )
 			{
 						observers.Add( observer );
 			}
 			public void RemoveObserver( IObserver observer )
 			{
 						observers.Remove( observer );
 			}
 			public void NotifyObserver()
 			{
 						foreach ( IObserver observer in observers )
 									observer.Update();
 			}
 			public string State{ get; set; }
 }

 class ConcreteObserver : IObserver
 {
 			string observerState;
 			IObservable subject;
 			public ConcreteObserver( IObservable subject )
 			{
 						this.subject = subject;
 			}
 			public void Update()
 			{
 						observerState = subject.State;
 			}
 }


