class Program
 {
 			static void Main()
 			{
 						Singleton uniqueGetInstance1 = Singleton.GetInstance();
 						Singleton uniqueGetInstance2 = Singleton.GetInstance();
 						Console.WriteLine( ReferenceEquals( uniqueGetInstance1, uniqueGetInstance2 ) );
 			}
 }

 class Singleton
 {
 			private static volatile Singleton uniqueGetInstance = null;
 			private static object syncRoot = new Object();
 			private Singleton() {}

 			public static Singleton GetInstance
 			{
 						get{
 									if ( uniqueGetInstance == null )
 									{
 												lock( syncRoot )
 												{
 															if ( uniqueGetInstance == null )
 																		uniqueGetInstance = new Singleton();
 												}
 									}
 									return uniqueGetInstance;
 						}						
 			}
 }

