class Program
 {
 			static void Main()
 			{
 						Enumerable enumerable = new Enumerable();

 						enumerable[ 0 ] = "Element A";
 						enumerable[ 1 ] = "Element B";
 						enumerable[ 2 ] = "Element C";
 						enumerable[ 3 ] = "Element D";

 						foreach (var item in enumerable)
 						{
 									Console.WriteLine( item );
 						}
 			}
 }

 interface IEnumerable
 {
 			IEnumerator GetEnumerаtor();
 }

 class Enumerable : IEnumerable
 {
 			private ArrayList items = new ArrayList();

 			public object this[ int index ]
 			{
 						get { return items[ index ]; }
 						set { items.Insert( index, value ); }
 			}

 			public int Count
 			{
 						get { return items.Count; }
 			}

 			// Реализация интерфейса IEnumerаble.
 			public IEnumerator GetEnumerаtor()
 			{
 						return new Enumerator( this );
 			}
 }

 interface IEnumerator
 {
 			bool MoveNext();
 			void Reset();
 			object Current { get; }
 }

 class Enumerator : IEnumerator
 {
 			private Enumerable enumerable;
 			private int current = -1;

 			public Enumerator( Enumerable enumerable )
 			{
 						this.enumerable = enumerable;
 			}

 			// Реализация интерфейса IEnumerаtor.
 			public bool MoveNext()
 			{
 						if ( current < enumerable.Count - 1 )
 						{
 									current++;
 									return true;
 						}
 						return false;
 			}

 			public void Reset()
 			{
 						current = -1;
 			}

 			public object Current
 			{
 						get { return enumerable[ current ]; }
 			}
 }

class Program
 {
