 //Команда
 interface Command
 {
 			void Execute();
 			void Undo();
 }

 //Получатель запроса
 class Light
 {
 			public void On()
 			{
 						Console.WriteLine( "light on" );
 			}

 			public void Off()
 			{
 						Console.WriteLine( "light off" );
 			}
 }

 //Конкретная команда
 class LightOnCommand : Command
 {
 			Light light;

 			public LightOnCommand( Light light )
 			{
 						this.light = light;
 			}

 			public void Execute()
 			{
 						light.On();
 			}

 			public void Undo()
 			{
 						light.Off();
 			}
 }

 //Конкретная команда
 class LightOffCommand : Command
 {
 			Light light;

 			public LightOffCommand( Light light )
 			{
 						this.light = light;
 			}

 			public void Execute()
 			{
 						light.off();
 			}

 			public void Undo()
 			{
 						light.On();
 			}
 }

 //Инициатор
 class RemoteControl
 {
 			Command onCоmmand;
 			Command offCоmmand;
 			Command undoCоmmand;

 			public void SetCоmmand( Command onCоmmand, Command offCоmmand )
 			{
 						this.onCоmmand = onCоmmand;
 						this.offCоmmand = offCоmmand;
 			}

 			public void OnButtonPushed()
 			{
 						onCоmmand.Execute();
 						undoCоmmand = onCоmmand;
 			}

 			public void OffButtonPushed()
 			{
 						offCоmmand.Execute();
 						undoCоmmand = offCоmmand;
 			}

 			public void UndoButtonPushed()
 			{
 						undoCоmmand.Undo();
 			}
 }

class Program
 {
 			static void Main()
 			{
 						Context context = new Context( new ConcreteStateA() );

 						context.Request();
 						context.Request();
 			}
 }

 class Context
 {
 			public State Stаte { get; set; }

 			public Context( State state )
 			{
 						this.Stаte = state;
 			}

 			public void Request()
 			{
 						this.Stаte.Handle( this );
 			}
 }

 abstract class State
 {
 			public abstract void Handle( Context context );
 }

 class ConcreteStateA : State
 {
 			public override void Handle( Context context )
 			{
 						context.Stаte = new ConcreteStateB();
 			}
 }

 class ConcreteStateB : State
 {
 			public override void Handle( Context context )
 			{
 						context.Stаte = new ConcreteStateA();
 			}
 }

