 						proger.DoWork();
 						proger.EarnMoney();

 						// пришел новый заказ, но теперь нужен c#
 						proger.Language = new CSharpLanguage();
 						proger.DoWork();
 						proger.EarnMoney();

 						// поручим задачу офисному программисту
 						proger = new CorporateProgrammer( new CPPLanguage());
 						proger.DoWork();
 						proger.EarnMoney();
 			}
 }

 interface ILanguage
 {
 			void Build();
 			void Execute();
 }

 class CPPLanguage : ILanguage
 {
 			public void Build()
 			{
 						Console.WriteLine( "С помощью компилятора C++ компилируем программу в бинарный код" );
 			}

 			public void Execute()
 			{
 						Console.WriteLine( "Запускаем исполняемый файл программы" );
 			}
 }

 class CSharpLanguage : ILanguage
 {
 			public void Build()
 			{
 						Console.WriteLine( "С помощью компилятора Roslyn компилируем исходный код в файл exe" );
 			}

 			public void Execute()
 			{
 						Console.WriteLine( "JIT компилирует программу бинарный код" );
 						Console.WriteLine( "CLR выполняет скомпилированный бинарный код" );
 			}
 }

 abstract class Programmer
 {
 			protected ILanguage language;
 			public ILanguage Language
 			{
 						set { language = value; }
 			}

 			public Programmer ( ILanguage lang )
 			{
 						language = lang;
 			}

 			public virtual void DoWork()
 			{
 						language.Build();
 						language.Execute();
 			}

 			public abstract void EarnMoney();
 }

 class FreelanceProgrammer : Programmer
 {
 			public FreelanceProgrammer( ILanguage lang ) : base( lang ) {}
 			public override void EarnMoney()
 			{
 						Console.WriteLine( "Получаем оплату за выполненный заказ" );
 			}
 }
 class CorporateProgrammer : Programmer
 {
 			public CorporateProgrammer( ILanguage lang ) : base( lang ) {}

 			public override void EarnMoney()
 			{
 						Console.WriteLine( "Получаем в конце месяца зарплату" );
 			}
 }

class Program
 {
 			public static void Main()
 			{
 						Builder builder = new ConcreteBuilder();

 						Director director = new Director( builder );
 						director.Construct();

 						Product product = builder.GetResult();
 						product.Show();
 			}
 }

 class Director
 {
 			Builder builder;

 			public Director( Builder builder )
 			{
 						this.builder = builder;
 			}

 			public void Construct()
 			{
 						builder.BuildPartA();
 						builder.BuildPartB();
 						builder.BuildPartC();
 			}
 }

 abstract class Builder
 {
 			public abstract void BuildPartA();
 			public abstract void BuildPartB();
 			public abstract void BuildPartC();
 			public abstract Product GetResult();
 }

 class ConcreteBuilder : Builder
 {
 			Product product = new Product();

 			public override void BuildPartA()
 			{
 						product.Add( "Part A" );
 			}

 			public override void BuildPartB()
 			{
 						product.Add( "Part B" );
 			}

 			public override void BuildPartC()
 			{
 						product.Add( "Part C" );
 			}

 			public override Product GetResult()
 			{
 						return product;
 			}
 }

 class Product
 {
 			ArrayList parts = new ArrayList();

 			public void Add( string part )
 			{
 						parts.Add( part );
 			}

 			public void Show()
 			{
 						foreach ( string part in parts )
 						Console.WriteLine( part );
 			}
 }

class Program
 {
 			static void Main()
 			{
 						Prototype prototype = null;						
 						Prototype clone = null;

 						prototype = new ConcretePrototype1( 1 );
 						clone = prototype.Clone();

 						prototype = new ConcretePrototype2( 2 );
 						clone = prototype.Clone();
 			}
 }

 abstract class Prototype
 {
 			public int Id { get; private set; }

 			public Prototype( int id )
 			{
 						this.Id = id;
 			}

 			public abstract Prototype Clone();
 }

 class ConcretePrototype1 : Prototype
 {
 			public ConcretePrototype1( int id ) : base( id ) {}

 			public override Prototype Clone()
 			{
 						return new ConcretePrototype1( Id );
 			}
 }

 class ConcretePrototype2 : Prototype
 {
 			public ConcretePrototype2( int id ) : base( id ) {}

 			public override Prototype Clone()
 			{
 						return new ConcretePrototype2( Id );
 			}
 }
